import React from 'react';

const NoteCard = ({ note, onEdit, onDelete }) => {
  return (
    <div className="bg-white shadow rounded p-4 my-2">
      <h3 className="font-bold text-lg">{note.title}</h3>
      <p className="text-gray-700">{note.content}</p>
      <div className="mt-2 space-x-2">
        <button onClick={() => onEdit(note)} className="text-blue-600 hover:underline">Edit</button>
        <button onClick={() => onDelete(note._id)} className="text-red-600 hover:underline">Delete</button>
      </div>
    </div>
  );
};

export default NoteCard;
