import React, { useState, useEffect, useContext } from 'react';
import axios from 'axios';
import NoteCard from '../components/NoteCard';
import NoteForm from '../components/NoteForm';
import toast from 'react-hot-toast';
import { AuthContext } from '../context/AuthContext';

const Dashboard = () => {
  const { token } = useContext(AuthContext);
  const [notes, setNotes] = useState([]);
  const [editingNote, setEditingNote] = useState(null);
  const [showForm, setShowForm] = useState(false);

  // Load notes: if user is logged in, fetch from backend; else use localStorage
  const fetchNotes = async () => {
    if (token) {
      try {
        const res = await axios.get('http://localhost:5000/api/notes', { headers: { 'x-auth-token': token } });
        setNotes(res.data);
      } catch (error) {
        toast.error('Error fetching notes');
      }
    } else {
      const guestNotes = JSON.parse(localStorage.getItem('notes')) || [];
      setNotes(guestNotes);
    }
  };

  useEffect(() => {
    fetchNotes();
  }, [token]);

  const saveNote = async (noteData) => {
    if (token) {
      // Registered user: call backend API
      try {
        if (noteData._id) {
          const res = await axios.put(`http://localhost:5000/api/notes/${noteData._id}`, noteData, { headers: { 'x-auth-token': token } });
          setNotes(notes.map(n => n._id === res.data._id ? res.data : n));
          toast.success('Note updated');
        } else {
          const res = await axios.post('http://localhost:5000/api/notes', noteData, { headers: { 'x-auth-token': token } });
          setNotes([res.data, ...notes]);
          toast.success('Note created');
        }
      } catch (error) {
        toast.error('Error saving note');
      }
    } else {
      // Guest: use localStorage
      let updatedNotes;
      if (noteData._id) {
        updatedNotes = notes.map(n => n._id === noteData._id ? noteData : n);
        toast.success('Note updated');
      } else {
        noteData._id = Date.now().toString();
        updatedNotes = [noteData, ...notes];
        toast.success('Note created');
      }
      setNotes(updatedNotes);
      localStorage.setItem('notes', JSON.stringify(updatedNotes));
    }
    setEditingNote(null);
    setShowForm(false);
  };

  const deleteNote = async (id) => {
    if (token) {
      try {
        await axios.delete(`http://localhost:5000/api/notes/${id}`, { headers: { 'x-auth-token': token } });
        setNotes(notes.filter(note => note._id !== id));
        toast.success('Note deleted');
      } catch (error) {
        toast.error('Error deleting note');
      }
    } else {
      const updatedNotes = notes.filter(note => note._id !== id);
      setNotes(updatedNotes);
      localStorage.setItem('notes', JSON.stringify(updatedNotes));
      toast.success('Note deleted');
    }
  };

  const startEdit = (note) => {
    setEditingNote(note);
    setShowForm(true);
  };

  return (
    <div className="max-w-3xl mx-auto my-10 p-4">
      <h2 className="text-3xl font-bold mb-4">Your Notes</h2>
      <button onClick={() => { setEditingNote(null); setShowForm(!showForm); }} className="mb-4 px-4 py-2 bg-blue-600 text-white rounded">
        {showForm ? 'Close Form' : 'Add New Note'}
      </button>
      {showForm && (
        <NoteForm 
          currentNote={editingNote}
          onSave={saveNote}
          onCancel={() => { setShowForm(false); setEditingNote(null); }}
        />
      )}
      <div className="mt-6">
        {notes.length > 0 ? (
          notes.map(note => (
            <NoteCard 
              key={note._id}
              note={note}
              onEdit={startEdit}
              onDelete={deleteNote}
            />
          ))
        ) : (
          <p className="text-gray-600">No notes found. {token ? 'Create a note to get started.' : 'Start by adding a note in guest mode.'}</p>
        )}
      </div>
    </div>
  );
};

export default Dashboard;
