import React, { useState, useEffect } from 'react';

const NoteForm = ({ currentNote, onSave, onCancel }) => {
  const [title, setTitle] = useState(currentNote ? currentNote.title : '');
  const [content, setContent] = useState(currentNote ? currentNote.content : '');

  useEffect(() => {
    if (currentNote) {
      setTitle(currentNote.title);
      setContent(currentNote.content);
    } else {
      setTitle('');
      setContent('');
    }
  }, [currentNote]);

  const handleSubmit = (e) => {
    e.preventDefault();
    onSave({ title, content, _id: currentNote?._id });
    setTitle('');
    setContent('');
  };

  return (
    <form onSubmit={handleSubmit} className="bg-gray-100 p-4 rounded shadow">
      <input
        type="text"
        placeholder="Title"
        className="w-full p-2 mb-2 border rounded"
        value={title}
        onChange={(e) => setTitle(e.target.value)}
      />
      <textarea
        placeholder="Your note here..."
        className="w-full p-2 mb-2 border rounded"
        rows="4"
        value={content}
        onChange={(e) => setContent(e.target.value)}
      ></textarea>
      <div className="flex justify-end space-x-2">
        <button type="button" onClick={onCancel} className="px-3 py-1 bg-gray-500 text-white rounded">Cancel</button>
        <button type="submit" className="px-3 py-1 bg-blue-600 text-white rounded">Save</button>
      </div>
    </form>
  );
};

export default NoteForm;
